func TestCertificateRequest(t *testing.T) {

	// Good
	var goodCR CertificateRequest
	err := json.Unmarshal(testCertificateRequestGood, &goodCR)
	if err != nil {
		t.Errorf("Error unmarshaling good certificate request: %v", err)
	}
	if err = VerifyCSR(goodCR.CSR); err != nil {
		t.Errorf("Valid CSR in CertificateRequest failed to verify: %v", err)
	}

	// Bad CSR
	var badCR CertificateRequest
	err = json.Unmarshal(testCertificateRequestBadCSR, &badCR)
	if err == nil {
		t.Errorf("Unexpectedly accepted certificate request with bad CSR")
	}

	// Marshal
	jsonCR, err := json.Marshal(goodCR)
	if err != nil {
		t.Errorf("Failed to marshal good certificate request: %v", err)
	}
	err = json.Unmarshal(jsonCR, &goodCR)
	if err != nil {
		t.Errorf("Marshalled certificate request failed to unmarshal: %v", err)
	}
}

func TestMergeChallenge(t *testing.T) {
	tls := true
	t1 := time.Now()
	t2 := time.Now().Add(-5 * time.Hour)
	challenge := Challenge{
		Type:      ChallengeTypeSimpleHTTP,
		Status:    StatusPending,
		Validated: &t1,
		Token:     "asdf",
	}
	response := Challenge{
		Type:      ChallengeTypeSimpleHTTP,
		Status:    StatusValid,
		Validated: &t2,
		Token:     "qwer",
		TLS:       &tls,
	}
	merged := Challenge{
		Type:      ChallengeTypeSimpleHTTP,
		Status:    StatusPending,
		Validated: &t1,
		Token:     "asdf",
		TLS:       &tls,
	}

	probe := challenge.MergeResponse(response)
	if probe.Status != merged.Status {
		t.Errorf("MergeChallenge allowed response to overwrite status")
	}
	if probe.Validated != merged.Validated {
		t.Errorf("MergeChallenge allowed response to overwrite completed time")
	}
	if probe.Token != merged.Token {
		t.Errorf("MergeChallenge allowed response to overwrite status")
	}
	if probe.TLS != merged.TLS {
		t.Errorf("MergeChallenge failed to overwrite TLS")
	}
}

